    background-size: cover;
    .header {
        position: absolute;
        top: 1rem;
        left: 50%;
        transform: translateX(-50%);
        z-index: 2;
        &__wrap {
            width: 60vw;
            padding: 10px;
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        &__left {
