                    0% {
                        top: 1px;
                    }
                    25% {
                        top: 2px;
                    }
                    50% {
                        top: 3px;
                    }
                    75% {
                        top: 2px;
                    }
                    100% {
                        top: 1px;
                    }
                }
            }
